import "./App.css";
import MyCamera from "./Components/MyCamera";
import ImageUploader from "./Components/ImageUploader";

function App(props) {
  return (
    <div>

      <div className="flex justify-center items-center flex-col py-10">
        <h1 className="text-center font-bold text-xl"> Your Plant </h1>
        <p> Status: Healthy </p>
        <img src="/plant.png" className="max-w-[70%]" />
      </div>

      {/* the camera component using a npm package; Ignored for now */}
      {/* <MyCamera /> */}
      <label className="mb-4 cursor-pointer bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600">
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="hidden"
        />
        Choose Image
      </label>
      <ImageUploader />
    </div>
  );
}

export default App;
