import "./App.css";
import MyCamera from "./Components/MyCamera";
import ImageUploader from "./Components/ImageUploader";

function App(props) {
  // for upload image 
  const [selectedImage, setSelectedImage] = useState(null);

  // Handle file input change
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  return (
    <div>
      <div className="flex justify-center items-center flex-col py-10">
        <h1 className="text-center font-bold text-xl"> Your Plant </h1>
        <p> Status: Healthy </p>
        <img src="/plant.png" className="max-w-[70%]" />
      </div>

      <div className="flex flex-col items-center">
        {/* the camera component using a npm package; Ignored for now */}
        {/* <MyCamera /> */}
        <button className="mb-4 cursor-pointer bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 w-[200px]">
          Take a picture
        </button>

        <button className="mb-4 cursor-pointer bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 w-[200px]">
          Upload image
        </button>

        <ImageUploader className="w-[200px]" />
      </div>
    </div>
  );
}

export default App;
