import "./App.css";
import { useState, useRef } from "react";
import ImagePreview from "./Components/ImagePreview";

function App(props) {
  // for upload image
  const [selectedImage, setSelectedImage] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const fileInputRef = useRef(null);
  // Function to close the popup
  const closePopup = () => {
    setIsOpen(false);
    setSelectedImage(null); // Clear the selected image
    if (fileInputRef.current) {
      fileInputRef.current.value = ""; // Reset the file input value
    }
  };

  // Handle file input change
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
        setIsOpen(true);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="">
      <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
        <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
          <h2 className="text-2xl font-bold text-green-600 mb-4">Welcome to Plant Assistant!</h2>
          <p className="text-gray-700 mb-4">
            ðŸŒ± Hi there! Plant Assistant is here to help you:
          </p>
          <ul className="text-gray-600 mb-4 list-disc list-inside space-y-2">
            <li>Identify potential plant diseases based on your photos.</li>
            <li>Double-check symptoms to ensure our suggestions match your plant's condition.</li>
            <li>Keep a record of your plant pictures for easy reference.</li>
          </ul>

          <label className="mb-4 text-center cursor-pointer bg-green-500 text-white py-2 px-4 rounded w-[200px]">
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            ref={fileInputRef} // for clearing the ref after closing the pop up 
            className="hidden"
          />
          Choose Image
        </label>
        </div>
      </div>

      {/* Image preview */}
      {isOpen && (
        <ImagePreview imgsrc = {selectedImage} handleClose={closePopup} />
      )}
    </div>
  );
}

export default App;
